import "./index.css";
import React from "react";
function App() {
  const [count, setCount] = React.useState(100500);
  const onClickFollow = () => {
    setCount(count + 1);
  };
  const onClickFollowing = () => {
    setCount(count - 1);
  };

  return (
    <div className="App">
      <div>
        <h2>777 tweets</h2>
        <h1>{count}</h1>
        <button onClick={onClickFollow}>FOLLOW</button>
        <button onClick={onClickFollowing}>FOLLOWING</button>
      </div>
    </div>
  );
}

export default App;
